// acme login with css stitching
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo, getDom} = require('@applitools/sdk-shared')

describe('Coverage Tests - AcmeLoginWithCssStitching', () => {
  let driver, destroyDriver, eyes

  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      displayName: 'acme login with css stitching',
      stitchMode: 'CSS',
      baselineName: 'TestAcmeLogin',
    })
  })

  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })

  it('acme login with css stitching', async () => {
    await spec.visit(driver, 'https://afternoon-savannah-68940.herokuapp.com/#')
    await eyes.open(driver, 'Eyes Selenium SDK - ACME', 'TestAcmeLogin', {
      width: 1024,
      height: 768,
    })

    await spec.type(
      driver,
      await spec.findElement(driver, '#username'),
      'adamC'
    )

    await spec.type(
      driver,
      await spec.findElement(driver, '#password'),
      'MySecret123?'
    )
    await eyes.check({region: '#username'})
    await eyes.check({region: '#password'})
    await eyes.close(undefined)
  })
})
