// appium android check region
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo, getDom} = require('@applitools/sdk-shared')

describe('Coverage Tests - AppiumAndroidCheckRegion', () => {
  let driver, destroyDriver, eyes

  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({
      device: 'Samsung Galaxy S8',
      app:
        'https://applitools.bintray.com/Examples/eyes-android-hello-world.apk',
    })
    eyes = testSetup.getEyes({
      displayName: 'appium android check region',
      baselineName: 'Appium_Android_CheckRegion',
      branchName: 'javascript_mobile',
    })
  })

  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })

  it('appium android check region (@native-selectors @mobile @native)', async () => {
    await eyes.open(
      driver,
      'Applitools Eyes SDK',
      'Appium_Android_CheckRegion',
      undefined
    )
    await eyes.check({
      region: {type: 'class name', selector: 'android.widget.Button'},
    })
    await eyes.close(undefined)
  })
})
