// appium iOS check region
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo, getDom} = require('@applitools/sdk-shared')

describe('Coverage Tests - AppiumIOSCheckRegion', () => {
  let driver, destroyDriver, eyes

  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({
      device: 'iPhone XS',
      app:
        'https://applitools.bintray.com/Examples/eyes-ios-hello-world/1.2/eyes-ios-hello-world.zip',
    })
    eyes = testSetup.getEyes({
      displayName: 'appium iOS check region',
      baselineName: 'Appium_iOS_CheckRegion',
      branchName: 'javascript_mobile',
    })
  })

  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })

  it('appium iOS check region (@native-selectors @mobile @native)', async () => {
    await eyes.open(
      driver,
      'Applitools Eyes SDK',
      'Appium_iOS_CheckRegion',
      undefined
    )
    await eyes.check({
      region: {
        type: '-ios predicate string',
        selector: "type == 'XCUIElementTypeButton'",
      },
    })
    await eyes.close(undefined)
  })
})
