// check frame after manual switch to frame with scroll stitching classic
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo, getDom} = require('@applitools/sdk-shared')

describe('Coverage Tests - CheckFrameAfterManualSwitchToFrameWithScrollStitchingClassic', () => {
  let driver, destroyDriver, eyes

  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      displayName:
        'check frame after manual switch to frame with scroll stitching classic',
      hideScrollbars: false,
      stitchMode: 'Scroll',
      baselineName: 'TestCheckInnerFrame_Scroll',
    })
  })

  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })

  it('check frame after manual switch to frame with scroll stitching classic (@webdriver)', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/FramesTestPage/'
    )
    await eyes.open(
      driver,
      'Eyes Selenium SDK - Classic API',
      'TestCheckInnerFrame_Scroll',
      {width: 700, height: 460}
    )
    await spec.executeScript(
      driver,
      'document.documentElement.scrollTop = 350',
      ...[]
    )
    const frame = await spec.findElement(driver, '[name="frame1"]')
    await spec.childContext(driver, frame)
    await eyes.checkFrame('frame1-1', undefined, undefined)
    await eyes.checkWindow(undefined, undefined, undefined)
    await spec.executeScript(
      driver,
      'document.body.style.background = "red"',
      ...[]
    )
    await eyes.checkWindow(undefined, undefined, undefined)
    await eyes.close(undefined)
  })
})
