// check region by selector in overflowed frame after manual scroll with css stitching
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo, getDom} = require('@applitools/sdk-shared')

describe('Coverage Tests - CheckRegionBySelectorInOverflowedFrameAfterManualScrollWithCssStitching', () => {
  let driver, destroyDriver, eyes

  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      displayName:
        'check region by selector in overflowed frame after manual scroll with css stitching',
      stitchMode: 'CSS',
      baselineName: 'TestCheckRegionInAVeryBigFrameAfterManualSwitchToFrame',
    })
  })

  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })

  it('check region by selector in overflowed frame after manual scroll with css stitching (@webdriver)', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/WixLikeTestPage/index.html'
    )
    await eyes.open(
      driver,
      'Eyes Selenium SDK - Special Cases',
      'TestCheckRegionInAVeryBigFrameAfterManualSwitchToFrame',
      {width: 700, height: 460}
    )

    await spec.childContext(
      driver,
      await spec.findElement(driver, '[name="frame1"]')
    )
    await eyes.check({region: 'img'})
    await eyes.close(undefined)
  })
})
