// check window after manual scroll on safari 11
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo, getDom} = require('@applitools/sdk-shared')

describe('Coverage Tests - CheckWindowAfterManualScrollOnSafari11', () => {
  let driver, destroyDriver, eyes

  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({
      browser: 'safari-11',
      legacy: true,
    })
    eyes = testSetup.getEyes({
      displayName: 'check window after manual scroll on safari 11',
      baselineName: 'CheckWindowAfterManualScrollOnSafari11',
    })
  })

  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })

  it('check window after manual scroll on safari 11 (@webdriver @safari)', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/FramesTestPage/'
    )
    await eyes.open(
      driver,
      'Eyes Selenium SDK - Classic API',
      'CheckWindowAfterManualScrollOnSafari11',
      {width: 700, height: 460}
    )
    await spec.executeScript(driver, 'window.scrollBy(0, 350)', ...[])
    await eyes.check({})
    await eyes.close(undefined)
  })
})
