// check window fully with html scrollRootElement after scroll when fail to scroll with css stitching
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo, getDom} = require('@applitools/sdk-shared')

describe('Coverage Tests - CheckWindowFullyWithHtmlScrollRootElementAfterScrollWhenFailToScrollWithCssStitching', () => {
  let driver, destroyDriver, eyes

  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      displayName:
        'check window fully with html scrollRootElement after scroll when fail to scroll with css stitching',
      stitchMode: 'CSS',
      baselineName:
        'TestCheckWindowFullyWithHtmlScrollRootElementAfterScrollWhenFailToScroll',
    })
  })

  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })

  it('check window fully with html scrollRootElement after scroll when fail to scroll with css stitching', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/FramesTestPage/'
    )
    await eyes.open(
      driver,
      'Eyes Selenium SDK - Fluent API',
      'TestCheckWindowFullyWithHtmlScrollRootElementAfterScrollWhenFailToScroll',
      {width: 700, height: 460}
    )
    await spec.executeScript(driver, 'window.scrollBy(0, 200)', ...[])
    await eyes.check({scrollRootElement: 'html', isFully: true})
    await eyes.close(undefined)
  })
})
