// should send dom and location when check frame fully
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo, getDom} = require('@applitools/sdk-shared')

describe('Coverage Tests - ShouldSendDomAndLocationWhenCheckFrameFully', () => {
  let driver, destroyDriver, eyes

  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      displayName: 'should send dom and location when check frame fully',
      baselineName: 'ShouldSendDomAndLocationWhenCheckFrameFully',
    })
  })

  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })

  it('should send dom and location when check frame fully', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/FramesTestPage/'
    )
    await eyes.open(
      driver,
      'Applitools Eyes SDK',
      'ShouldSendDomAndLocationWhenCheckFrameFully',
      {width: 700, height: 460}
    )
    const frameElement = await spec.findElement(driver, '[name="frame1"]')
    await spec.executeScript(
      driver,
      'arguments[0].setAttribute("data-expected-frame", "true");',
      ...[frameElement]
    )
    await spec.executeScript(
      driver,
      'arguments[0].contentDocument.documentElement.setAttribute("data-expected-scroll", "true");',
      ...[frameElement]
    )
    await eyes.check({frames: [frameElement], isFully: true})
    const result = await eyes.close(false)
    const info = await getTestInfo(result)
    assert.deepStrictEqual(
      info['actualAppOutput']['0']['image']['hasDom'],
      true,
      undefined
    )
    const dom = await getDom(
      result,
      info['actualAppOutput']['0']['image']['domId']
    )
    const scrollingElements = dom.getNodesByAttribute('data-applitools-scroll')
    assert.deepStrictEqual(scrollingElements['length'], 1, undefined)
    assert.deepStrictEqual(
      scrollingElements['0']['attributes']['data-applitools-scroll'],
      'true',
      undefined
    )
    assert.deepStrictEqual(
      scrollingElements['0']['attributes']['data-expected-scroll'],
      'true',
      undefined
    )
    const frameElementInDom = dom.getNodesByAttribute('data-expected-frame')

    assert.deepStrictEqual(
      info['actualAppOutput']['0']['image']['location']['x'],
      Math.round(frameElementInDom['0']['rect']['left'] + 2) || 0,
      undefined
    )

    assert.deepStrictEqual(
      info['actualAppOutput']['0']['image']['location']['y'],
      Math.round(frameElementInDom['0']['rect']['top'] + 2) || 0,
      undefined
    )
  })
})
