// should send dom and location when check region by selector fully with vg
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo, getDom} = require('@applitools/sdk-shared')

describe('Coverage Tests - ShouldSendDomAndLocationWhenCheckRegionBySelectorFullyWithVg', () => {
  let driver, destroyDriver, eyes

  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      vg: true,
      displayName:
        'should send dom and location when check region by selector fully with vg',
      baselineName:
        'ShouldSendDomAndLocationWhenCheckRegionBySelectorFullyWithVg',
    })
  })

  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })

  it('should send dom and location when check region by selector fully with vg', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/FramesTestPage/'
    )
    await eyes.open(
      driver,
      'Applitools Eyes SDK',
      'ShouldSendDomAndLocationWhenCheckRegionBySelectorFullyWithVg',
      {width: 700, height: 460}
    )
    await spec.executeScript(driver, 'window.scrollTo(0, 350)', ...[])
    const scrollableElement = await spec.findElement(driver, '#overflowing-div')
    await spec.executeScript(
      driver,
      'arguments[0].setAttribute("data-expected-target", "true");',
      ...[scrollableElement]
    )
    await spec.executeScript(
      driver,
      'arguments[0].setAttribute("data-expected-scroll", "true");',
      ...[scrollableElement]
    )
    await eyes.check({region: scrollableElement, isFully: true})
    const result = await eyes.close(false)
    const info = await getTestInfo(result)
    assert.deepStrictEqual(
      info['actualAppOutput']['0']['image']['hasDom'],
      true,
      undefined
    )
    const dom = await getDom(
      result,
      info['actualAppOutput']['0']['image']['domId']
    )
    const scrollingElements = dom.getNodesByAttribute('data-applitools-scroll')
    assert.deepStrictEqual(scrollingElements['length'], 1, undefined)
    assert.deepStrictEqual(
      scrollingElements['0']['attributes']['data-applitools-scroll'],
      'true',
      undefined
    )
    assert.deepStrictEqual(
      scrollingElements['0']['attributes']['data-expected-scroll'],
      'true',
      undefined
    )
    const targetElements = dom.getNodesByAttribute('data-expected-target')

    assert.deepStrictEqual(
      info['actualAppOutput']['0']['image']['location']['x'],
      Math.round(targetElements['0']['rect']['left'] + 2) || 0,
      undefined
    )

    assert.deepStrictEqual(
      info['actualAppOutput']['0']['image']['location']['y'],
      Math.round(targetElements['0']['rect']['top'] + 2) || 0,
      undefined
    )
  })
})
